function parseCSV(t){t=t.split("\n");const s=t[0].split(",");return t.slice(1).map(t=>{const e=t.split(","),r={};return s.forEach((t,s)=>{r[t.trim()]=e[s].trim()}),r})}function shuffleArray(s){for(let t=s.length-1;0<t;t--){var e=Math.floor(Math.random()*(t+1));[s[t],s[e]]=[s[e],s[t]]}return s}fetch("https://docs.google.com/spreadsheets/d/e/2PACX-1vQlBwUvFAaHZIF_qkWGa-1chrXhLWOgH4W1wfk6obyT-U6e4p42J-LQDLuImQ8JT3OT35qqDVNPWOEW/pub?output=csv").then(t=>t.text()).then(t=>{t=shuffleArray(function(t){const s=t.split("\n"),a=s[0].split(",");return s.slice(1).map(t=>{const e=t.match(/(".*?"|[^",]+)(?=\s*,|\s*$)/g),r={};return a.forEach((t,s)=>{r[t.trim()]=e[s].trim()}),r})}(t));const e=document.getElementById("flashcards__container");t.forEach(t=>{const s=document.createElement("div");s.classList.add("flashcards__card"),s.innerHTML=`
        <div class="flashcards__card--question">${t.question}</div>
        <div class="flashcards__card--answer">${void 0!==t.answer?t.answer:""}</div>
      `,s.addEventListener("click",()=>{s.classList.toggle("flipped")}),e.appendChild(s)})});